(()=>{document.getElementsByClassName("container")[0];const e=document.querySelectorAll(".card");$.get("https://students-api.2.us-1.fl0.io/movies",(n=>{n.forEach(((n,t)=>{const i=e[t];if(i){const e=`\n    <div class="pelicula">\n    <img src="${n.poster}"> \n        <div class="detalle"> \n        <h2>${n.title}</h2>\n        <p>Director: ${n.director}</p>\n        <p>Year: ${n.year},Genre: ${n.genre.join(", ")}, Duration: ${n.duration}</p>\n        <p>Duration: ${n.duration},Rating: ${n.rate}</p>\n        <div class="btn"><h6>Ver</h6></div>\n        </div>\n    </div>\n    `;i.innerHTML=e,i.classList.remove("card"),i.classList.add("card")}}))}))})();